mapper.select:
SELECT

,

FROM Trail
WHERE LOWER(name) LIKE #{keywordPattern}
OR LOWER(location) LIKE #{keywordPattern}
OR LOWER(difficulty) LIKE #{keywordPattern}
OR LOWER(scenery) LIKE #{keywordPattern}
OR LOWER(description) LIKE #{keywordPattern}
ORDER BY
CASE
WHEN LOWER(name) LIKE #{keywordPattern} THEN 0
ELSE 1
END,
name ASC
LIMIT 50
